mapper.select:
select * from coupon_template


        and name like concat('%',#{name},'%')


        and type = #{type}


        and status = #{status}


order by create_time desc

mapper.update:
update coupon_template
name = #{name}, type = #{type}, discount_type = #{discountType}, discount_value = #{discountValue}, min_amount = #{minAmount}, max_discount = #{maxDiscount}, total_count = #{totalCount}, used_count = #{usedCount}, per_user_limit = #{perUserLimit}, valid_days = #{validDays}, start_time = #{startTime}, end_time = #{endTime}, status = #{status}, update_time = #{updateTime}, update_time = now(), update_user = #{updateUser},
where id = #{id}
